import { connect } from "react-redux";
import { addPackage } from '../../../../store/actions/newAuction';
const AddPackage = (props) => {

    let newPackage;
    return (props.showSetPackage ? (
        <form>
            <div className="ui equal width form">
                <div className="fields">
                    <div className="field">
                        <label>Quantity of tickets</label>
                        <input type="number" onChange={(e) => newPackage.qty = e.target.value} />
                    </div>
                    <div className="field">
                        <label>Discount percentages</label>
                        <input type="number" required={(newPackage.qty > 0)} onChange={(e) => newPackage.discount = e.target.value} />

                    </div>
                </div>
            </div>
            <button className="positive ui button" onClick={() => { props.addPackage(newPackage) }}>Add new Package</button>
        </form>
    ) : null);
}
const mapStateToProps = (state) => {
    return {
        showSetPackage: state.auction.showSetPackage
    };
}
export default connect(mapStateToProps, { addPackage })(AddPackage);
