import React, { useState } from 'react';
import ReactDOM from 'react-dom'
//קבוצות רכישה
const PurchasingGroups = () => {

    const [qty, setQty] = React.useState();
    let func = (e) => {
        setQty(e.target.value)
        for (let i = 1; i <= qty; i++) {
            <input type="button" key={i} />;
        }
    }
    return (<div class="fields">
        <h1>Purchasing Group</h1>
        {/* קבוצת רכישה היא קבוצת כרטיסים בעלי אותו מחיר */}
        <label>A purchasing group is a group of tickets with the same price</label>
        <div class="field">
            <label>Quantity</label>
            <input type="number" onChange={(e) => { func() }} required="true" />
        </div>

    </div>
    );

}

export default PurchasingGroups;
