import {connect}from 'react';


const UserLogin = (props) => {
    let fullName = "";
    let password = "";

    // checkUser = () => {
     
    
    // }
    return (
        // <form>
        //     <input type="text" placeholder="Username" onChange={(e) => { fullName = e.target.value }} required="true" />
        //     <input type="password" placeholder="" onChange={(e) => { password = e.target.value }} required="true" />
        //     <input className="ui button" type="submit" value="Login"  />

        // </form>
<div class="ui placeholder segment">
  <div class="ui two column very relaxed stackable grid">
    <div class="column">
      <div class="ui form">
        <div class="field">
          <label>Username</label>
          <div class="ui left icon input">
            <input type="text" placeholder="Username"onChange={(e) => { fullName = e.target.value }} required="true" />
            <i class="user icon"></i>
          </div>
        </div>
        <div class="field">
          <label>Password</label>
          <div class="ui left icon input">
            <input type="password" onChange={(e) => { password = e.target.value }} required="true"/>
            <i class="lock icon"></i>
          </div>
        </div>
        <div class="ui blue submit button">Login</div>
      </div>
    </div>
    <div class="middle aligned column">
      <div class="ui big button">
        <i class="signup icon"></i>
        Sign Up
      </div>
    </div>
  </div>
  <div class="ui vertical divider">
    Or
  </div>
</div>

    );

}


export default UserLogin;
