import React, { useState } from 'react';
import ReactDOM from 'react-dom'
//קבוצות רכישה
let qty = 0;
let qty_arr = [];
const AddGroup = () => {
    return (<div class="field">
        <form>
            <label>Quantity</label>
            <input type="number" onChange={(e) => qty = e.target.value} required="true" min="1" />
            <input type="submit" value="add" onClick={qty_arr.push(qty)} />
        </form>
    </div>)
}
const PurchasingGroups = () => {
    return (
        <>
            <h1>Purchasing Group</h1>
            {/* קבוצת רכישה היא קבוצת כרטיסים בעלי אותו מחיר */}
            <label>A purchasing group is a group of tickets with the same price</label>
            <div class="ui equal width form">
                <div class="fields">
                    <AddGroup />
                    <input type="button" value="Add Purchasing Group" onClick={() => {return <AddGroup />}} />
                </div>
            </div>
        </>);
}

export default PurchasingGroups;
