import React, { useState } from 'react';
import ReactDOM from 'react-dom'
//קבוצות רכישה
const PurchasingGroups = () => {
    let qty = 0;
    let func = () => {
        for (let i = 1; i <= committedFieldsToAdd; i++) {
          <Field id={i} key={i} />;
        }
    }
    // <div class="fields">
    //     <h1>Purchasing Group</h1>
    //     {/* קבוצת רכישה היא קבוצת כרטיסים בעלי אותו מחיר */}
    //     <label>A purchasing group is a group of tickets with the same price</label>
    //     <div class="field">
    //         <label>Quantity</label>
    //         <input type="number" onChange={(e) => { qty = e.target.value }} required="true" />
    //     </div>

    //     {for(let i=0;i<qty; i++){

    //     }?null}


    // </div>
    const [
        inputfieldsToAdd,
        setInputfieldsToAdd,
    ] = React.useState();
    const [
        committedFieldsToAdd,
        setCommittedFieldsToAdd,
    ] = React.useState(0);
    return (
        <>
            <div>
                <div>
                    <label>Number of fields to add</label>
                    <input
                        type="number"
                        value={inputfieldsToAdd}
                        onChange={(e) =>
                            setInputfieldsToAdd(
                                parseInt(e.currentTarget.value, 10)
                            )
                        }
                    />
                </div>
                <button onClick={() => { setCommittedFieldsToAdd(inputfieldsToAdd); }}>
                    Add fields
              </button>

            </div>
            {func()}
        </>
    );

}
const Field = ({ id }) => (
    <div>
        <label htmlFor={`field${id}`}>Field {id}</label>
        <input id={`field${id}`} type="text" />
    </div>
);
export default PurchasingGroups;
